INSERT INTO test_definitions VALUES(32,'texteval','coerenza_narrativa','Coerenza narrativa',replace(replace('Valuta il seguente testo concentrandoti sulla COERENZA NARRATIVA. Devi identificare eventuali contraddizioni interne, eventi impossibili o incoerenze logiche. Restituisci SOLO un valore da 1 a 10 in testo semplice, con al massimo 2 caratteri. Non aggiungere altro. Non essere generoso con la valutazione.
INSERT INTO test_definitions VALUES(33,'texteval','struttura','Struttura',replace(replace('Valuta il seguente testo concentrandoti sulla STRUTTURA. Devi rilevare l''assenza di conflitto, l''assenza di sviluppo e una conclusione mancante. Restituisci il punteggio da 1 a 10  in testo semplice, con al massimo 2 caratteri, non aggiungere altro. Non essere generoso con la valutazione.
INSERT INTO test_definitions VALUES(34,'texteval','caratterizzazione_personaggi','Caratterizzazione',replace(replace('Valuta la CARATTERIZZAZIONE DEI PERSONAGGI. Devi verificare se i personaggi hanno voce propria, motivazioni e stile coerente, oppure se parlano tutti allo stesso modo. Restituisci il punteggio da 1 a 10  in testo semplice, con al massimo 2 caratteri, non aggiungere altro.
INSERT INTO test_definitions VALUES(35,'texteval','dialoghi','Dialoghi',replace(replace('Valuta la qualità dei DIALOGHI. Devi rilevare dialoghi finti, innaturali, inutili o che non fanno avanzare la scena. Restituisci solo il punteggio da 1 a 10 in testo semplice, con al massimo 2 caratteri, non aggiungere altro.
INSERT INTO test_definitions VALUES(36,'texteval','ritmo','Ritmo',replace(replace('Valuta il RITMO narrativo. Devi rilevare rallentamenti eccessivi, descrizioni inutili e mancanza di dinamica. Rispondi con il punteggio da 1 a 10, con al massimo 2 caratteri, non aggiungere altro.
INSERT INTO test_definitions VALUES(37,'texteval','originalita','Originalità 1',replace(replace('Valuta l''ORIGINALITÀ della storia. Devi identificare cliché, formule narrative abusate o mancanza di idee originali. Rispondi con la valutazione da 1 a 10 in testo semplice, con al massimo 2 caratteri, non aggiungere altro.
INSERT INTO test_definitions VALUES(38,'texteval','stile','Stile 1',replace(replace('Valuta lo STILE del testo. Devi rilevare frasi ripetitive, monotone o struttura sempre identica. Rispondi solo con il voto da 1 a 10, con al massimo 2 caratteri, non aggiungere altro.
INSERT INTO test_definitions VALUES(39,'texteval','worldbuilding','Worldbuilding 1',replace(replace('Valuta il WORLDBUILDING. Devi rilevare incoerenze nel mondo, regole non rispettate o impossibili. Rispondi solo con la valutazione da 1 a 10, con al massimo 2 caratteri, non aggiungere altro.
